                        .i_i2c_debug_p_gen(),
                        .i_i2c_debug_rd(),
                        .i_i2c_debug_s_gen(),
                        .i_i2c_debug_slave_act(),
                        .i_i2c_debug_slv_cstate(),
                        .i_i2c_debug_wr(),
                        .i_i2c_ic_activity_intr(),
                        .i_i2c_ic_clk_oe(),
                        .i_i2c_ic_current_src_en(),
