module system_top (
  // CLOCK AND RESETS ------------------
  input          HCLK_top,               // Clock
  input          HRESETn_top,         // Asynchronous reset
  output          SYSRESETREQ_top_out,         // Asynchronous reset
  output          LOCKUP_top_out,         // Asynchronous reset
  output          TXEV_top_out         // Asynchronous reset

);

//------------------------------------------------------------------------------
// Internal Signals
//------------------------------------------------------------------------------
    // AHB-LITE CORTEX M0 INTERFACE  --------------
    wire [31:0] HADDR_top;             // AHB transaction address
    wire [ 2:0] HBURST_top;            // AHB burst: tied to single
    wire        HMASTLOCK_top;         // AHB locked transfer (always zero)
    wire [ 3:0] HPROT_top;             // AHB protection: priv; data or inst
    wire [ 2:0] HSIZE_top;             // AHB size: byte, half-word or word
    wire [ 1:0] HTRANS_top;            // AHB transfer: non-sequential only
    wire [31:0] HWDATA_top;            // AHB write-data
    wire        HWRITE_top;            // AHB write control
    wire [31:0] HRDATA_top;            // AHB read-data
    wire        HREADY_top;            // AHB stall signal
    wire [1:0]  HRESP_top;             // AHB error response
    wire 	    NMI_top;	       // Non-maskable interrupt
    wire [15:0] IRQ_top;	       // Interrupt inputs
    wire 	    RXEV_top;	       // External events input
    wire 	    TXEV_top;	       // External events output
    wire 	    SLEEPING_top;	      // Core and NVIC sleeping output 
    wire 	    LOCKUP_top;	      // Core and NVIC sleeping output 
    wire 	    SYSRESETREQ_top;	      // Core and NVIC sleeping output 
    // PWM ACCELERATOR INTERFACE
    wire [31:0] PWM_hrdata_top;
    wire PWM_hready_resp_top;
    wire [1:0] PWM_hresp_top;
    wire [31:0] PWM_haddr_top;
    wire [2:0] PWM_hburst_top;
    wire PWM_hmastlock_top;
    wire [3:0] PWM_hprot_top;
    wire PWM_hready_top;
    wire PWM_hsel_top;
    wire [2:0] PWM_hsize_top;
    wire [1:0] PWM_htrans_top;
    wire [31:0] PWM_hwdata_top;
    wire PWM_hwrite_top;
    wire [7:0] out_pwm_top;
    // PID ACCELERATOR INTERFACE
    wire [31:0] PID_hrdata_top;
    wire PID_hready_resp_top;
    wire [1:0] PID_hresp_top;
    wire [31:0] PID_haddr_top;
    wire [2:0] PID_hburst_top;
    wire PID_hmastlock_top;
    wire [3:0] PID_hprot_top;
    wire PID_hready_top;
    wire PID_hsel_top;
    wire [2:0] PID_hsize_top;
    wire [1:0] PID_htrans_top;
    wire [31:0] PID_hwdata_top;
    wire PID_hwrite_top;
    wire [24:0] dout_0_pid_top;
    wire [24:0] dout_1_pid_top;

    //SPI INTERFACE
   wire          i_ssi_rxd_top;
   wire          i_ssi_ss_in_n_top;
   wire          i_ssi_ssi_clk_top;
   wire          i_ssi_ssi_rst_n_top;
   wire         i_ssi_sclk_out_top;
   wire         i_ssi_ss_0_n_top;
   wire         i_ssi_ssi_mst_intr_n_top;
   wire         i_ssi_ssi_oe_n_top;
   wire         i_ssi_ssi_rxf_intr_n_top;
   wire         i_ssi_ssi_rxo_intr_n_top;
   wire         i_ssi_ssi_rxu_intr_n_top;
   wire         i_ssi_ssi_sleep_top;
   wire         i_ssi_ssi_txe_intr_n_top;
   wire         i_ssi_ssi_txo_intr_n_top;
   wire         i_ssi_txd_top;
    //RAM INTERFACE
    wire [31:0] RAM_hrdata_top;
    wire RAM_hready_resp_top;
    wire [1:0] RAM_hresp_top;
    wire [31:0] RAM_haddr_top;
    wire [2:0] RAM_hburst_top;
    wire RAM_hmastlock_top;
    wire [3:0] RAM_hprot_top;
    wire RAM_hready_top;
    wire RAM_hsel_top;
    wire [2:0] RAM_hsize_top;
    wire [1:0] RAM_htrans_top;
    wire [31:0] RAM_hwdata_top;
    wire RAM_hwrite_top;
    
//Assign wires
assign NMI_top = 1'b0;
assign IRQ_top = {16{1'b0}};
assign RXEV_top = 1'b0;
assign SYSRESETREQ_top_out = SYSRESETREQ_top;
assign TXEV_top_out = TXEV_top;
assign LOCKUP_top_out = LOCKUP_top;
//------------------------------------------------------------------------------
// Instantiate coreAssembler-generated AMBA IP
//------------------------------------------------------------------------------
