                        .i_ssi_ssi_mst_intr_n(i_ssi_ssi_mst_intr_n_top),
                        .i_ssi_ssi_oe_n(i_ssi_ssi_oe_n_top),
                        .i_ssi_ssi_rxf_intr_n(i_ssi_ssi_rxf_intr_n_top),
                        .i_ssi_ssi_rxo_intr_n(i_ssi_ssi_rxo_intr_n_top),
