    .ex_i_ahb_AHB_Slave_PID_hwdata(PID_hwdata_top),
    .ex_i_ahb_AHB_Slave_PID_hwrite(PID_hwrite_top),
    // PID out
    .dout_0_pid(dout_0_pid_top),
    .dout_1_pid(dout_1_pid_top)
);

